mapper.insert:
MERGE INTO SHOP_CART
USING DUAL
ON (ITEM_CODE = #{itemCode} AND MEM_ID = #{memId})
WHEN MATCHED THEN
	UPDATE
	SET
	ITEM_CNT = ITEM_CNT + #{itemCnt}
	WHERE MEM_ID = #{memId}
WHEN NOT MATCHED THEN
	INSERT (
	    CART_NUM
	    , ITEM_CODE
	    , MEM_ID
	    , ITEM_CNT
	) VALUES (
	    (SELECT NVL(MAX(CART_NUM), 0) + 1
	    FROM SHOP_CART)
	    , #{itemCode}
	    , #{memId}
	    , #{itemCnt}
	)
